$(function(){var targetNode=document.getElementById("harvestsimulator");if(targetNode){var config={attributes:true,childList:true};var callback=function(mutationsList){mutationsList.forEach(function(element){if(element.type==="childList")$(window).trigger("heightChanged");else if(element.type==="attributes")$(window).trigger("heightChanged")})};var observer=new MutationObserver(callback);observer.observe(targetNode,config)}});